import { initializeApp } from 'firebase/app';
import { getAuth, signInWithRedirect, signInWithPopup, GoogleAuthProvider, createUserWithEmailAndPassword,
          signInWithEmailAndPassword,
          signOut,
          onAuthStateChanged
            } from 'firebase/auth';

import { getFirestore, doc, getDoc, setDoc, collection, writeBatch } from 'firebase/firestore';

const firebaseConfig = {
    apiKey: "AIzaSyCtkWGP390YbooWUf3vF9ra8yGlmpMgM0o",
    authDomain: "crwn-clothing-db-f04b9.firebaseapp.com",
    projectId: "crwn-clothing-db-f04b9",
